    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        Anggota1841720003Mayang agt = new Anggota1841720003Mayang();
        agt.setmIdAnggotaMayang(Integer.parseInt(txtIdAnggota.getText()));
        agt.setmNamaMayang(txtNama.getText());
        agt.setmAlamatMayang(txtAlamat.getText());
        agt.setmTeleponMayang(txtTelepon.getText());
        agt.saveMayang();
        txtIdAnggota.setText(Integer.toString(agt.getmIdAnggotaMayang()));
        tampilkanDataMayang();
    }//GEN-LAST:event_btnSimpanActionPerformed

    private void btnHapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHapusActionPerformed
        DefaultTableModel model = (DefaultTableModel)tblAnggota.getModel();
        int row = tblAnggota.getSelectedRow();
        
        Anggota1841720003Mayang agt = new Anggota1841720003Mayang().getByIdMayang(Integer.parseInt(model.getValueAt(row, 0).toString()));
        agt.deleteMayang();
        kosongkanFormMayang();
        tampilkanDataMayang();
    }//GEN-LAST:event_btnHapusActionPerformed

    private void btnTambahBaruActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTambahBaruActionPerformed
        kosongkanFormMayang();
    }//GEN-LAST:event_btnTambahBaruActionPerformed

    private void btnCariActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCariActionPerformed
        cariMayang(txtCari.getText());
    }//GEN-LAST:event_btnCariActionPerformed

    private void tblAnggotaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblAnggotaMouseClicked
        DefaultTableModel model = (DefaultTableModel)tblAnggota.getModel();
        int row = tblAnggota.getSelectedRow();
        
        txtIdAnggota.setText(model.getValueAt(row, 0).toString());
        txtNama.setText(model.getValueAt(row, 1).toString());
        txtAlamat.setText(model.getValueAt(row, 2).toString());
        txtTelepon.setText(model.getValueAt(row, 3).toString());
    }//GEN-LAST:event_tblAnggotaMouseClicked

