    public void kosongkanFormMayang(){
        txtIdAnggota.setText("0");
        txtNama.setText("");
        txtAlamat.setText("");
        txtTelepon.setText("");
    }
    
    public void tampilkanDataMayang(){
        String[] kolom = {"ID", "Nama", "Keterangan", "Telepon"};
        ArrayList<Anggota1841720003Mayang> list = new Anggota1841720003Mayang().getAllMayang();
        Object rowData[] = new Object[4];
        
        tblAnggota.setModel(new DefaultTableModel(new Object[][]{}, kolom));
        
        for(Anggota1841720003Mayang agt : list){
            rowData[0] = agt.getmIdAnggotaMayang();
            rowData[1] = agt.getmNamaMayang();
            rowData[2] = agt.getmAlamatMayang();
            rowData[3] = agt.getmTeleponMayang();
            
            ((DefaultTableModel)tblAnggota.getModel()).addRow(rowData);
        }
    }
    
    public void cariMayang(String keyword){
        String[] kolom = {"ID", "Nama", "Keterangan"};
        ArrayList<Anggota1841720003Mayang> list = new Anggota1841720003Mayang().search(keyword);
        Object rowData[] = new Object[4];
        
        tblAnggota.setModel(new DefaultTableModel(new Object[][]{}, kolom));
        
        for(Anggota1841720003Mayang agt : list){
            rowData[0] = agt.getmIdAnggotaMayang();
            rowData[1] = agt.getmNamaMayang();
            rowData[2] = agt.getmAlamatMayang();
            rowData[3] = agt.getmTeleponMayang();
            
            ((DefaultTableModel)tblAnggota.getModel()).addRow(rowData);
        }
    }
    
